import React, { useEffect, useState } from 'react'

const Contact = () => {

  

  const [userData, setUserData] = useState(0);


  const userContact = async () => {
    try {
      const res = await fetch('/getdata', {
        action: "GET",
        headers: {
          "Content-Type": "application/json"
        },
      });

      const data = await res.json();
      console.log(data);
      setUserData(data);

      if(!res.status === 200) {
        const error = new Error(res.error);
        throw error;
      }

    } catch (err) {
      console.log(err);
      

    }
  }

  useEffect (() => {
    userContact();
  }, 
  );


  return (
   <>
    <div className='contact_info'>
      <div className='container-fluid'>
        <div className='row'>
          <div className='col-lg-10 offset-lg-1 d-flex justify-content-between'>
            {/* phone number */}
            <div className='contact_info_item d-flex justify-content-start align-items-center'>
              <img src='' alt='phone' />
              <div className='contact_info_content'>
                <div className='contact_info_title'>
                  Phone

                </div>
                <div className='contact_info_text'>
                  +91 98765 43210

                </div>

              </div>

            </div>

             {/* email */}
             <div className='contact_info_item d-flex justify-content-start align-items-center'>
              <img src='' alt='email' />
              <div className='contact_info_content'>
                <div className='contact_info_title'>
                  Email

                </div>
                <div className='contact_info_text'>
                  demo@gmail.com

                </div>

              </div>

            </div>

             {/* address */}
             <div className='contact_info_item d-flex justify-content-start align-items-center'>
              <img src='' alt='address' />
              <div className='contact_info_content'>
                <div className='contact_info_title'>
                  Address

                </div>
                <div className='contact_info_text'>
                  Vadodara, GJ, India

                </div>

              </div>

            </div>

          </div>

        </div>

      </div>

    </div>

    {/* contact us form */}

    <div className='contact_form'>
      <div className='container'>
        <div className='row'>
          <div className='col-lg-10 offset-lg-1'>
            <div className='contact_form_container py-5'>
              <div className='contact_form_title'>
              Get In Touch </div>
              <form id='contact_form'>
                <div className='contact_form_name d-flex justify-content-between align-item-between'>
                  <input type="text" id='contact_form_name'
                  className='contact_form_name input_field' 
                  placeholder='Your Name' required='true'/>

                  <input type="email" id='contact_form_email'
                  className='contact_form_email input_field' 
                  placeholder='Your Email' required='true'/>

                  <input type="number" id='contact_form_phone'
                  className='contact_form_phone input_field' 
                  placeholder='Your Phone Number' required='true'/>

                </div>
                <div className='contact_form_text mt-5'>
                  <textarea className='text_field contact_form_message' 
                  placeholder='Message' cols='123' rows='10'></textarea>

                </div>
                <div className='contact_form_button'>
                  <button type='submit' className='button contact_submit_button'>Send Message</button>

                </div>

              </form>
            </div>

          </div>

        </div>

      </div>

    </div>
   </>
  )
}

export default Contact
